mapper.select:

SELECT COUNT(*)
FROM HANJU_USER
WHERE USER_ID = #{userId}

SELECT * 
FROM HANJU_USER
WHERE USER_ID = #{userId} AND PASSWORD = #{password}

mapper.update:
UPDATE HANJU_USER
SET POINT = POINT - #{point}
WHERE USER_ID = #{userId}

mapper.delete:
DELETE
FROM HANJU_FAVORITE
WHERE USER_ID = #{userId} AND PRODUCT_ID = #{productId}
